						if ( $sidebar_available ) {
							$widget_message_type = 'success';
							$widget_message      = esc_html__( 'Imported', 'foxiz-core' );
						} else {
							$widget_message_type = 'warning';
							$widget_message      = esc_html__( 'Imported to Inactive', 'foxiz-core' );
						}
					}

					$results[ $sidebar_id ]['widgets'][ $widget_instance_id ]['name']         = isset( $available_widgets[ $id_base ]['name'] ) ? $available_widgets[ $id_base ]['name'] : $id_base; // widget name or ID if name not available (not supported by site)
					$results[ $sidebar_id ]['widgets'][ $widget_instance_id ]['title'] = ! empty( $widget->title ) ? $widget->title : esc_html__( 'No Title', 'foxiz-core' );                 // show "No Title" if widget instance is untitled
					$results[ $sidebar_id ]['widgets'][ $widget_instance_id ]['message_type'] = $widget_message_type;
					$results[ $sidebar_id ]['widgets'][ $widget_instance_id ]['message']      = $widget_message;
				}
			}

			$this->flag_as_imported[ strval( $this->directory ) ]['widgets'] = true;
			do_action( 'rb_importer_after_widgets', $this->directory, $this->main_path );

			return $results;
		}

		/**
		 * @param $file
		 */
		public function set_taxonomies_data( $file ) {

			if ( file_exists( $file ) ) {
				WP_Filesystem();
				global $wp_filesystem;
				$data = $wp_filesystem->get_contents( $file );
				$data = json_decode( stripslashes_deep( trim( $data ) ), true );
				$data = maybe_unserialize( $data );
			} else {
				$data = wp_remote_get( $file, $this->remote_args() );
				if ( ! is_wp_error( $data ) ) {
					$data = wp_remote_retrieve_body( $data );
					$data = json_decode( stripslashes_deep( trim( $data ) ), true );
					$data = maybe_unserialize( $data );
				} else {
					return;
				}
			}
			if ( ! empty( $data ) && is_array( $data ) ) {
				$current_tax = get_option( 'cptui_taxonomies', [] );
				if ( empty( $current_tax ) ) {
					update_option( 'cptui_taxonomies', $data );
				} else {
					$new_post_types = array_merge( $current_tax, $data );
					update_option( 'cptui_taxonomies', $new_post_types );
				}

				if ( function_exists( 'cptui_create_custom_taxonomies' ) ) {
					cptui_create_custom_taxonomies();
				}
			}
		}

		/**
		 * @param $file
		 */
		public function set_post_types_data( $file ) {

			if ( file_exists( $file ) ) {
				WP_Filesystem();
				global $wp_filesystem;
				$data = $wp_filesystem->get_contents( $file );
				$data = json_decode( stripslashes_deep( trim( $data ) ), true );
				$data = maybe_unserialize( $data );
			} else {
				$data = wp_remote_get( $file, $this->remote_args() );
				if ( ! is_wp_error( $data ) ) {
					$data = wp_remote_retrieve_body( $data );
					$data = json_decode( stripslashes_deep( trim( $data ) ), true );
					$data = maybe_unserialize( $data );
				} else {
					return;
				}
			}

			if ( ! empty( $data ) && is_array( $data ) ) {
				$current_post_type = get_option( 'cptui_post_types', [] );
				if ( empty( $current_post_type ) ) {
					update_option( 'cptui_post_types', $data );
				} else {
					$new_post_types = array_merge( $current_post_type, $data );
					update_option( 'cptui_post_types', $new_post_types );
				}

				/** trigger */
				if ( function_exists( 'cptui_create_custom_post_types' ) ) {
					cptui_create_custom_post_types();
				}
			}
		}

	}
}