if ( ! function_exists( 'foxiz_get_svg' ) ) {
	/**
	 * @param string $svg_name
	 * @param string $color
	 * @param string $ui
	 *
	 * @return false|string
	 * get svg icon
	 */
	function foxiz_get_svg( $svg_name = '', $color = '', $ui = '' ) {

		$output = Foxiz_SVG_Icons::get_svg( $svg_name, $color, $ui );

		if ( empty( $ui ) ) {
			$output = wp_kses( $output,
				[
					'svg'     => [
						'class'       => true,
						'xmlns'       => true,
						'width'       => true,
						'height'      => true,
						'viewbox'     => true,
						'aria-hidden' => true,
						'role'        => true,
						'focusable'   => true,
					],
					'rect'    => [
						'width'  => true,
						'height' => true,
					],
					'path'    => [
						'fill'              => true,
						'fill-rule'         => true,
						'd'                 => true,
						'transform'         => true,
						'stroke'            => true,
						'stroke-width'      => true,
						'stroke-miterlimit' => true,
					],
					'circle'  => [
						'fill' => true,
						'cx'   => true,
						'cy'   => true,
						'r'    => true,
					],
					'polygon' => [
						'fill'      => true,
						'fill-rule' => true,
						'points'    => true,
						'transform' => true,
						'focusable' => true,
					],
				]
			);
		}

		if ( ! $output ) {
			return false;
		}

		return $output;
	}
}

if ( ! function_exists( 'foxiz_render_svg' ) ) {
	/**
	 * @param string $svg_name
	 * @param string $color
	 * @param string $ui
	 * render svg
	 */
	function foxiz_render_svg( $svg_name = '', $color = '', $ui = '' ) {

		echo foxiz_get_svg( $svg_name, $color, $ui );
	}
}

if ( ! class_exists( 'Foxiz_SVG_Icons', false ) ) {
	/**
	 * Class Foxiz_SVG_Icons
	 */
	class Foxiz_SVG_Icons {

		public static function get_svg( $icon, $color = '', $ui = '' ) {

			switch ( $ui ) {
				case 'reaction' :
					$data = self::$reaction_icons;
					break;
				case 'tweet' :
					$data = self::$tweet_icons;
					break;
				case 'address' :
					$data = self::$address_icons;
					break;
				case 'animation' :
					$data = self::$animation;
					break;
				default:
					$data = self::$ui_icons;
			}

			$data = apply_filters( 'foxiz_add_svg', $data );

			if ( array_key_exists( $icon, $data ) ) {
				$repl   = '<svg class="svg-icon svg-' . $icon . '" aria-hidden="true" role="img" focusable="false" ';
				$output = preg_replace( '/^<svg /', $repl, trim( $data[ $icon ] ) );
				if ( ! empty( $color ) ) {
					$output = str_replace( 'currentColor', $color, $output );
				}
				$output = preg_replace( "/([\n\t]+)/", ' ', $output );
				$output = preg_replace( '/>\s*</', '><', $output );

				return $output;
			}

			return false;
		}

